sp_widget.client_script:
function($rootScope,$scope){
  /* widget controller */
  var c = this;
	$rootScope.$on('SelectNote',function(event,data){
		console.log("Caught Note ID",$rootScope.noteID);
		c.server.get({action:'getNote',noteID:$rootScope.noteID}).then(function(r){
			console.log(r.data);
			c.data.title = r.data.noteObj.title;
			c.data.note = r.data.noteObj.note;
			c.data.noteID = r.data.noteObj.sys_id;			
		});
	});
	c.updateNote = function(updateType){

		console.log('Update note function called with the type '+updateType);
		c.server.get({action:'updateNote',noteID:c.data.noteID, noteTitle:c.data.title,noteBody:c.data.note}).then(function(r){
			//Update the view now
			var updateDict = {"title":c.data.title,"note":c.data.note};
			$rootScope.updateDictionary = updateDict;
			$rootScope.$emit('NoteUpdated',updateDict);
		});
	}
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
if(input && input.noteID){//check if input has some value and noteID is present
	var noteGR = new GlideRecord('x_snc_createnotes_note');	
	if(noteGR.get(input.noteID)){
		if(input.action=='getNote'){
		data.noteObj = {};
		$sp.getRecordDisplayValues(data.noteObj,noteGR,"title,note,sys_id");
		}
		else if(input.action=='updateNote'){
		gs.info('Inside updateType');
		noteGR.title=input.noteTitle;
		noteGR.note=input.noteBody;
		noteGR.update();
	}
	}
}
})();
