sp_widget.client_script:
function($scope,$rootScope, spUtil, spAriaUtil, spModal) {
  /* widget controller */
  var c = this;
	//spUtil.addInfoMessage("This is my info message");
  //spAriaUtil.sendLiveMessage("Hello World");

	$scope.currentIndex = -1;
	c.noteSelected = function(idx){
		console.log("Index is "+idx);
		$scope.currentIndex = idx
		var id = c.data.notes[idx].sys_id;
		console.log("noteID is "+id);
		$rootScope.noteID = id;
		$rootScope.$emit('SelectNote', id);
	}

	$rootScope.$on('NoteUpdated',function(event,data){
		console.log("caught update note event "+$rootScope.updateDictionary);
		console.log("Value of $index is "+$scope.currentIndex);
		var objAtIndex = c.data.notes[$scope.currentIndex];
		objAtIndex.title=$rootScope.updateDictionary.title;
		objAtIndex.note=$rootScope.updateDictionary.note;
		c.data.notes[$scope.currentIndex]=objAtIndex;
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var gr = new GlideRecord('x_snc_createnotes_note');
	gr.addQuery('user',gs.getUser().getID());
	gr.orderByDesc('sys_created_on');
	gr.query();
	while(gr.next()){
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj,gr,'number,title,sys_id');
		noteObj.note = gr.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}
})();
